<?php
// [ux_banner]
function uxbannerShortcode( $atts, $content = null ){
  $bannerid = rand();
  extract( shortcode_atts( array(
    'text_pos' => 'center',
    'height' => '300px',
    'text_color' => 'light',
    'link' => '',
    'text_width' => '60%',
    'text_align' => 'center',
    'text_box' => '',
    'animation' => 'fadeIn',
    'effect' => '',
    'video_mp4' => '',
    'video_ogg' => '',
    'video_webm' => '',
    'video_sound' => 'false',
    'hover' => '',
    'bg' => '', 
    'parallax' => ''
  ), $atts ) );

  ob_start();

  // replace ___ (3 underscores) with a nice divider
  $fix = array (
                '&nbsp;' => '', 
                '<p></p>' => '', 
                '<p>[' => '[', 
                ']</p>' => ']', 
                ']<br />' => ']',
                '_____' => '<div class="tx-div large"></div>',
                '____' => '<div class="tx-div medium"></div>',
                '___' => '<div class="tx-div small"></div>',
  );
   $content = strtr($content, $fix);

   $content = do_shortcode( $content ); 

   $img_link = get_template_directory_uri().'/img/';

   $color = "light";
   if($text_color == 'light') $color = "dark";

   if($hover) $hover = 'hover_'.$hover;

   $animated = "";
   if($animation != "none") $animated = "animated";

   $start_link = "";
   $end_link = "";
   if($link) {$start_link = '<a href="'.$link.'">'; $end_link = '</a>';};

   $background = "";
   $background_color = "";
    if (strpos($bg,'http://') !== false || strpos($bg,'https://') !== false) {
      $background = $bg;
    }
    elseif (strpos($bg,'#') !== false) {
      $background_color = 'background-color:'.$bg.'!important';
    }
     else {
      $bg = wp_get_attachment_image_src($bg, 'large');
      $background = $bg[0];
    }

   $textalign = "";
   if($text_align) {$textalign = "text-".$text_align;}
    
   $parallax_class = '';
   if($parallax){$parallax_class = 'ux_parallax'; $parallax='data-velocity="0.'.$parallax.'"';} 
 
  ?>

   <div id="banner_<?php echo $bannerid; ?>" class="ux_banner <?php if($text_box)echo 'ux-textbox-'.$text_box; ?> <?php echo $color; ?> <?php echo $hover; ?>"  style="height:<?php echo $height; ?>" data-height="<?php echo $height; ?>" role="banner">
      <?php echo $start_link; ?>
       <div class="banner-bg <?php echo $parallax_class; ?>" <?php echo $parallax; ?> style="background-image:url(<?php echo $background; ?>); <?php echo $background_color; ?>"></div>
       <?php if($video_mp4 || $video_webm || $video_ogg){ ?>
       <div class="video-overlay" style="position:absolute;top:0;bottom:0;right:0;left:0;z-index:2;background-image: url('data:image/png;base64,');"></div>
       <video class="ux-banner-video hide-for-small" style="position:absolute;top:0;left:0;bottom:0;right:0;min-width: 100%;min-height: 100%;z-index:1;" poster="<?php echo $background; ?>" preload="auto" autoplay="" loop="loop" <?php if($video_sound == 'false') echo "muted='muted'"; ?>>
            <source src="<?php echo $video_mp4; ?>" type="video/mp4">
            <source src="<?php echo $video_webm; ?>" type="video/webm">
            <source src="<?php echo $video_ogg; ?>" type="video/ogg">
        </video>
        <?php } ?>
       <?php if($effect){ ?>
          <div class="banner-effect"></div>
       <?php } ?>
        <div class="row">
          <div class="inner <?php echo $text_pos; ?>  <?php echo $animated; ?>  <?php echo $textalign; ?> " data-animate="<?php echo $animation; ?>" style="width:<?php echo $text_width; ?>">
           <?php echo $content; ?>
          </div>  
        </div>
       <?php echo $end_link; ?>

<?php // EFFECTS // ?>
<?php if($effect){ ?>
<?php if($effect === 'snow'){ ?>
<style>
@keyframes snow { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}
@-moz-keyframes snow { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}
@-webkit-keyframes snow { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% {background-position: 500px 1000px, 400px 400px, 300px 300px;
    }
}
@-ms-keyframes snow { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}
#banner_<?php echo $bannerid; ?> .banner-effect {
    background-image: 
      url('data:image/png;base64,'),
      url('data:image/png;base64,');    -webkit-animation: snow 20s linear infinite;
    -moz-animation: snow 20s linear infinite;
    -ms-animation: snow 20s linear infinite;
    animation: snow 20s linear infinite;
}
</style>
<?php } // End snow effect ?>


<?php if($effect === 'confetti'){ ?>
<style>
@keyframes confetti { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}
@-moz-keyframes confetti { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}
@-webkit-keyframes confetti { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% {background-position: 500px 1000px, 400px 400px, 300px 300px;}
}
@-ms-keyframes confetti { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }

    100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}
#banner_<?php echo $bannerid; ?> .banner-effect {
    background-image: 
      url('data:image/png;base64,'),
      url('data:image/png;base64,');    
      -webkit-animation: confetti 10s linear infinite;
    -moz-animation: confetti 10s linear infinite;
    -ms-animation: confetti 10s linear infinite;
    animation: confetti 10s linear infinite;
}
</style>
<?php } // End confetti effect ?>

<?php if($effect === "sliding-glass"){ ?>
<style>
@keyframes glass { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px}
}
@-moz-keyframes glass { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }

    100% { background-position: 500px 1000px, 400px 400px}
}
@-webkit-keyframes glass { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% {background-position: 500px 1000px, -400px -400px; }
}
@-ms-keyframes glass { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: 500px 1000px, 400px 400px }
}
#banner_<?php echo $bannerid; ?> .banner-effect {
    background-image: 
     url('data:image/png;base64,'),
    url('data:image/png;base64,');
    -webkit-animation: glass 30s linear infinite;
    -moz-animation: glass 30s linear infinite;
    -ms-animation: glass 30s linear infinite;
    animation: glass 30s linear infinite;
}
</style>
<?php } // End sliding glass effect ?>


<?php if($effect === 'sparkle'){ ?>
<style>
@keyframes sparkle { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: -500px -1000px, -400px -400px, 300px 300px }
}
@-moz-keyframes sparkle { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: -500px -1000px, -400px -400px, 300px 300px }
}
@-webkit-keyframes sparkle { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% {background-position: -500px -1000px, -200px -400px, 300px 300px;
    }
}
@-ms-keyframes sparkle { 
    0% { background-position: 0px 0px, 0px 0px, 0px 0px }
    100% { background-position: -500px -1000px, -400px -400px, 300px 300px }
}
#banner_<?php echo $bannerid; ?> .banner-effect {
    background-image: 
      url('data:image/png;base64,'),
      url('data:image/png;base64,');
    -webkit-animation: sparkle 60s linear infinite;
    -moz-animation: sparkle 60s linear infinite;
    -ms-animation: sparkle 60s linear infinite;
    animation: sparkle 60s linear infinite;
}
</style>
<?php } // End sparkle effect ?>



<?php if($effect === 'rain'){ ?>
<style>
@keyframes rain { 
    0% { background-position: 0px 0px }
    100% { background-position: 500px 1000px }
}
@-moz-keyframes rain { 
    0% { background-position: 0px 0px }
    100% { background-position: 500px 1000px }
}
@-webkit-keyframes rain { 
    0% { background-position: 0px 0px }
    100% {background-position: 500px 1000px;
    }
}
@-ms-keyframes rain { 
    0% { background-position: 0px 0px}
    100% { background-position: 500px 1000px}
}
#banner_<?php echo $bannerid; ?> .banner-effect {
  
background-image: url('data:image/png;base64,');

      -webkit-animation: rain 2s linear infinite;
    -moz-animation: rain 2s linear infinite;
    -ms-animation: rain 2s linear infinite;
    animation: rain 2s linear infinite;
}
</style>
<?php } // End sparkle effect ?>
<?php } ?>



</div><!-- end .ux_banner -->

<?php 

  $content = ob_get_contents();
  ob_end_clean();
  return $content;
 
}
add_shortcode('ux_banner', 'uxbannerShortcode');
